pragma solidity ^0.4.11;

import 'zeppelin-solidity/contracts/ownership/Ownable.sol';
import 'zeppelin-solidity/contracts/token/BasicToken.sol';
import 'zeppelin-solidity/contracts/math/SafeMath.sol';

/**
 * @title 
 * @dev BasicToken contract which allows token holders to charge their wallet with some wei.
 * This mecanism is aimed at providing the GAS needed for running ERC20 wallets.
 */
contract ChargeableToken is BasicToken, Ownable {
  using SafeMath for uint256;

  uint256 public weiContractCapacity = 0;
  uint256 public weiProvidedPerWallet = 0;
  uint256 public tokenPricePerCharge = 0;

  function setWeiContractCapacity(uint256 _weiContractCapacity) onlyOwner public {
    weiContractCapacity = _weiContractCapacity;
  }

  function setWeiProvidedPerWallet(uint256 _weiProvidedPerWallet) onlyOwner public {
    weiProvidedPerWallet = _weiProvidedPerWallet;
  }

  function setTokenPricePerCharge(uint256 _tokenPricePerCharge) onlyOwner public {
    tokenPricePerCharge = _tokenPricePerCharge;
  }

  function chargeWallet() public {
    require(tokenPricePerCharge > 0);
    require(msg.sender.balance <= weiProvidedPerWallet);

    // SafeMath.sub will throw if there is not enough balance.
    balances[msg.sender] = balances[msg.sender].sub(tokenPricePerCharge);
    balances[owner] = balances[owner].add(tokenPricePerCharge);

    if(!msg.sender.send(weiProvidedPerWallet)){
      revert();
    } 
  }

  function rechargeContract() public payable {
    // The contract's balance has already been updated
    require(this.balance <= weiContractCapacity);
  }
}

