pragma solidity ^0.4.11;

import 'zeppelin-solidity/contracts/ownership/Ownable.sol';
import 'zeppelin-solidity/contracts/token/BasicToken.sol';
import 'zeppelin-solidity/contracts/math/SafeMath.sol';

/**
 * @title IssuableToken
 * @dev BasicToken contract which implement an issuing mechanism.
 */
contract IssuableToken is BasicToken, Ownable {
  using SafeMath for uint256;

  uint256 public allTimeIssued = 0;
  uint256 public allTimeRedeemed = 0;

  /**
   * @dev called by the owner to increase the supply
   */
  function issue(uint256 _amount) onlyOwner public {
    balances[owner]=balances[owner].add(_amount);
    totalSupply=totalSupply.add(_amount);

    allTimeIssued=allTimeIssued.add(_amount);
  }

  /**
   * @dev called by the owner to decrease the supply
   */
  function redeem(uint256 _amount) onlyOwner public {
    balances[owner]=balances[owner].sub(_amount);
    totalSupply=totalSupply.sub(_amount);

    allTimeRedeemed=allTimeRedeemed.add(_amount);
  }
}
